sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
if (isLoading) {
return;
}

if(newValue == 'sim'){
g_form.setDisplay('pergunta_3', false);
g_form.clearValue('pergunta_3');
g_form.setDisplay('pergunta_4', true);
}

else if(newValue == 'nao'){
g_form.clearValue('pergunta_4');
g_form.setDisplay('pergunta_4', false);
g_form.setDisplay('pergunta_3', true);
}

else if(newValue == ''){
g_form.clearValue('pergunta_3');
g_form.clearValue('pergunta_4');
g_form.setDisplay('pergunta_3', false);
g_form.setDisplay('pergunta_4', false);
}


//Type appropriate comment here, and begin script below

}
