sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
if (isLoading || newValue === '') {
	return;
}
// Verifica se o formato do CEP digitado é válido
if(!(isNaN(newValue)) && newValue.length==8) {
var gaViaCep = new GlideAjax('global.getAddressByZIPCode');
// Envia o valor do campo CEP com parâmetro para o Script Include
gaViaCep.addParam('sysparm_cep',g_form.getValue("zip_code"));
// Function a ser chamada no Script Include
gaViaCep.addParam('sysparm_name','getAddress');
gaViaCep.getXML(updateCampus);
} else {
// Limpa os campos de endereço
clearAddressFields();
showMessage("zip_code","Invalid Postal Code Format. Please, enter a valid postal code (eg: 12345678).","error");
}
}



function updateCampus(response) {
var answer = response.responseXML.documentElement.getAttribute("answer");

if(answer) {
// Avalia se a resposta retornada é um JSON válido
var returneddata = answer.evalJSON(true);

// Limpa as mensagens do form
g_form.clearMessages();



// Limpa os campos de endereço
clearAddressFields();

// Verifica se o ViaCep retornou uma resposta válida (CEP encontrado)
if(!returneddata.erro) {
// Atribui os valores retornados aos campos do formulário
g_form.setValue("address",returneddata.logradouro);
g_form.setValue("neighborhood",returneddata.bairro);
g_form.setValue("city",returneddata.localidade);
g_form.setValue("estate",returneddata.uf);
} else {
// CEP não encontrado no ViaCep
showMessage("zip_code","Postal Code not found at ViaCep","info");
}
} else {
// Não houve resposta para a consulta
// Formato de CEP inválido para o ViaCep
showMessage("zip_code","Invalid Postal Code Code Format. Please, enter a valid postal code (eg: 12345678).","error");
}
}



function clearAddressFields() {
// Limpa as mensagens do form
g_form.clearMessages();
// Limpa os campos de endereço
g_form.clearValue("address");
g_form.clearValue("neighborhood");
g_form.clearValue("city");
g_form.clearValue("estate");
}



function showMessage(field,message,type) {
// Limpa as mensagens do form
g_form.clearMessages();
if(type=="error") {
// Mensagem no topo do form
g_form.addErrorMessage(message);
} else if(type=="info") {
g_form.addInfoMessage(message);
}
// Mensagem no campo Zip Code
g_form.showFieldMsg(field,message,type);
}
