sys_script_include.script:
var getAddressByZIPCode = Class.create();
getAddressByZIPCode.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

getAddress: function() {
// Recebe o valor do campo CEP passado pelo Client Script
var cep = this.getParameter('sysparm_cep');

try {

// Instanciando RESTMessage
var rmViaCEP = new sn_ws.RESTMessageV2();
// Configurando EndPoint
rmViaCEP.setEndpoint('https://viacep.com.br/ws/' + cep + '/json/');
// Configurando mÃ©todo HTTP: GET
rmViaCEP.setHttpMethod('GET');
// Executando a consulta
var response = rmViaCEP.execute();
// Body da resposta
var responseBody = response.getBody();
// Status da resposta
var httpStatus = response.getStatusCode();
// Retornando a resposta para o caller
return responseBody;
} catch (ex) {
var message = ex.message;
}

},

type: 'getAddressByZIPCode'
});
