sys_script_include.script:
var bootcamper_utils = Class.create();
bootcamper_utils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

getMembers: function(){
var applicationList = '';
var grMembers = new GlideRecord('sys_user');
grMembers.addQuery('company', '31bea3d53790200044e0bfc8bcbe5dec');
grMembers.query();
while(grMembers.next()){
if(applicationList.length > 0){
applicationList+= (','+grMembers.sys_id)+'';
}else{
applicationList+= (grMembers.sys_id);
}
}
return applicationList;
},

getExternal: function(){
var applicationList = '';
var grMembers = new GlideRecord('x_938900_bootcampe_email');
grMembers.query();
while(grMembers.next()){
if(applicationList.length > 0){
applicationList+= (','+grMembers.email)+'';
}else{
applicationList+= (grMembers.email);
}
}
return applicationList;
},

type: 'bootcamper_utils'
});
