sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

// implement resource here
var respObj = {}; //declare the response object
var payload = request.body.data;  //retrieving the JSON body
var solicitante = payload.chave_solicitante;
var info = payload.informacoes_adicionais;

var grUser = new GlideRecord('sys_user');
grUser.addQuery('user_name', solicitante);
grUser.query();
while(grUser.next()){
var user_id = grUser.sys_id;
}

var grBtc = new GlideRecord("x_938900_bootcampe_bootcampers");
grBtc.initialize();
grBtc.solicitante = solicitante;
grBtc.informacoes_adicionais = info;
grBtc.chave_solicitante = solicitante;
var btc_id = grBtc.insert();

var grTsk = new GlideRecord("service_task");
grTsk.initialize();
grTsk.parent = btc_id;
var tsk_id = grTsk.insert();

this.status = '200';
respObj.body = {
"message": "Creation Success!",
"detail": "",
"case number": grBtc.number,
"task number": grTsk.number
};

if(tsk_id){
response.setBody(respObj);
response.setStatus(this.status);
return response;
}
else{
var myError = new sn_ws_err.ServiceError();
myError.setStatus(442);
myError.setMessage('Something gone wrong');
myError.setDetail('Something gone wrong.');
return myError;
}

})(request, response);
