sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

// implement resource here
var respObj = {}; //declare the response object
var payload = request.body.data;  //retrieving the JSON body
var executante = payload.chave_executante;
var case_number = payload.case_number;
var task_number = payload.task_number;
var grupo_executante = payload.grupo_executante;

var grCase = new GlideRecord("x_938900_bootcampe_bootcampers");
grCase.addQuery('number', case_number);
grCase.query();

while(grCase.next()){
grCase.assigned_to = executante;
grCase.assignment_group = grupo_executante;
grCase.update();
}

var grTask = new GlideRecord("service_task");
grTask.addQuery('number', task_number);
grTask.query();

while(grTask.next()){
grTask.assigned_to = executante;
grTask.assignment_group = grupo_executante;
grTask.update();
}

this.status = '200';
respObj.body = {
"message": "Update Success!",
"detail": ""
};

response.setBody(respObj);
response.setStatus(this.status);
return response;

})(request, response);
